<template>
  <div class="select-none overflow-y-scroll">
   
    <div>
      <ForgotPassword  @set-loader="setLoader"  v-if="modal['forgotPassword']" @trigger-modal="modalStat" />
    </div>
    <div >
      <PageLoader  v-if="loader" />
    </div>
    <div  v-if="pages['sigin']">
      <LoginPage  @set-loader="setLoader" @trigger-modal="modalStat" @goto-page="Page" />
    </div>
    <div v-if="pages['signup']">
      <SignUpPage @set-loader="setLoader" @goto-page="Page"/>
    </div>
    <div v-if="pages['home']">
      <WebPage :profile_img="user.img_url" @set-loader="setLoader" :question_box="modal.qbox_val"/>
    </div>
    
  </div>
 
 
</template>

<script>
import LoginPage from './components/Login.vue';
import SignUpPage from './components/SignUp.vue';
import WebPage from './components/page.vue';
import ForgotPassword from './components/forgotPassword.vue';
import PageLoader from './components/loader.vue'




import {firebase, storage, isUserOnline} from '@/main.js';


export default {
  name: 'App',
  components: {
   
    LoginPage,
    SignUpPage,
    WebPage,
    PageLoader,
    ForgotPassword
  },
  methods:{
    questionDialogue(modal){
        this.modal.question_dialogue = modal.stat;
        this.modal.qbox_val = modal.value;

    },
    modalStat(stat){
      this.modal['forgotPassword'] = stat;
      
    },
    setLoader(opt){
      this.loader = opt
    },
    Page(page){
      switch(page){
        case 'signin': this.clearPage(); this.pages['sigin'] = true; break;
        case 'signup': this.clearPage(); this.pages['signup'] = true; break;
        case 'homepage': this.clearPage(); this.pages['home'] = true; break;
      }
      
    },clearPage(){
        this.pages['home'] = false;
        this.pages['sigin'] = false;
        this.pages['signup'] = false;
    }
  },
  mounted:async function(){
    if(await isUserOnline()){
      this.setLoader(true);
      let auth = await firebase.auth_user();
        if(auth){
          let user_info = await firebase.getUserInfo(auth.uid);
          
          this.user = user_info;
          storage.setJSON('auth_user', user_info);
          
          this.clearPage();
          this.pages['home'] = true;
        

        }else{
          this.clearPage();
          this.pages['sigin'] = true;
        }
        this.setLoader(false);
        this.$toast.success('You are online', {position:'top-right'});
    }else{
      let user_info = storage.getJSON('auth_user');
      if(user_info){
        this.user = user_info
            
        this.clearPage();
          this.pages['home'] = true;

      }else{
        this.clearPage();
          this.pages['sigin'] = true;
      }
        this.$toast.error('You are offline some features are not available',  {position:'top-right'});
    }

    {
    localStorage.setItem('img/eaquabill/defaultimg/profile','data:image/jpeg;base64,');
    }
   

  }, data(){
    return {
      loader:false,
      user:{

      },
     
      pages:{
        sigin:true,
        signup:false,
        home:false
        
      },
      modal:{
        forgotPassword:false,
        question_dialogue:false,
        qbox_val:false
      }
    }
  }
}
</script>

